CREATE TABLE MEMBER (USER_ID VARCHAR(30) NOT NULL PRIMARY KEY, NAME VARCHAR(50) NOT NULL, PASSWORD VARCHAR(255) NOT NULL, BIRTH DATE NOT NULL, EMAIL VARCHAR(50) NOT NULL, PHONE VARCHAR(13), USER_SE VARCHAR(30) NOT NULL);
CREATE TABLE BOARD (board_id INT AUTO_INCREMENT PRIMARY KEY, author VARCHAR(50) NOT NULL, title VARCHAR(200) NOT NULL, content TEXT NOT NULL, created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, allow_comment BOOLEAN NOT NULL DEFAULT TRUE, comment_cnt INT NOT NULL DEFAULT 0, views INT NOT NULL DEFAULT 0, FOREIGN KEY (author) REFERENCES MEMBER(NAME));


