mapper.select:
SELECT * FROM BOARD
AND title LIKE CONCAT('%', #{searchText}, '%') AND author LIKE CONCAT('%', #{searchText}, '%') AND content LIKE CONCAT('%', #{searchText}, '%')
ORDER BY board_id DESC LIMIT #{firstIndex}, #{pageUnit}

mapper.update:
		UPDATE BOARD
		SET views = views + 1
     	WHERE board_id = #{boardId}

mapper.select:
SELECT * FROM BOARD_FILE
WHERE post_id = #{postId}

	AND file_seq = #{fileSeq}

ORDER BY file_seq;

SELECT * FROM BOARD_FILE
WHERE post_id = #{postId}
AND file_seq = #{fileSeq}
ORDER BY file_seq;

mapper.update:
UPDATE BOARD
SET comment_cnt = (
	SELECT COUNT(*) FROM COMMENTS
	WHERE board_id = #{boardId}
)
WHERE board_id = #{boardId}
AND allow_comment = 1

UPDATE BOARD
SET title = #{title},
	content = #{content},
	created_at = NOW(),
	allow_comment = #{allowComment}
WHERE board_id = #{boardId}
